{
  "description": "Model selection configuration for different task types",
  "tasks": {
    "simple_refactor": {
      "description": "Basic code refactoring, variable renaming, formatting",
      "model": "claude-3-haiku",
      "temperature": 0.2,
      "maxContext": 4000,
      "includeFiles": [
        "current_file_only"
      ],
      "systemPrompt": "You are refactoring code. Make minimal changes. Preserve all functionality."
    },
    "bug_fix": {
      "description": "Debugging and fixing specific issues",
      "model": "claude-3-sonnet",
      "temperature": 0.3,
      "maxContext": 8000,
      "includeFiles": [
        "current_file",
        "related_tests",
        "error_logs"
      ],
      "systemPrompt": "You are debugging. Focus on the root cause. Provide minimal fix."
    },
    "feature_implementation": {
      "description": "Implementing new features",
      "model": "claude-3-opus",
      "temperature": 0.4,
      "maxContext": 16000,
      "includeFiles": [
        "feature_files",
        "related_patterns",
        "tests"
      ],
      "systemPrompt": "You are implementing a new feature. Follow existing patterns. Write tests."
    },
    "architecture_design": {
      "description": "System design and architectural decisions",
      "model": "claude-3-opus",
      "temperature": 0.5,
      "maxContext": 32000,
      "includeFiles": [
        "all_patterns",
        "architecture_docs",
        "existing_structure"
      ],
      "systemPrompt": "You are designing system architecture. Consider scale, maintainability, and existing patterns."
    },
    "code_review": {
      "description": "Reviewing code for issues and improvements",
      "model": "claude-3-sonnet",
      "temperature": 0.3,
      "maxContext": 12000,
      "includeFiles": [
        "pr_files",
        "related_tests",
        "style_guide"
      ],
      "systemPrompt": "You are reviewing code. Look for bugs, security issues, and style violations."
    },
    "test_generation": {
      "description": "Creating comprehensive test suites",
      "model": "claude-3-sonnet",
      "temperature": 0.2,
      "maxContext": 8000,
      "includeFiles": [
        "implementation",
        "existing_tests",
        "test_patterns"
      ],
      "systemPrompt": "You are writing tests. Cover edge cases. Follow AAA pattern. Use existing test utilities."
    },
    "documentation": {
      "description": "Writing or updating documentation",
      "model": "claude-3-haiku",
      "temperature": 0.4,
      "maxContext": 6000,
      "includeFiles": [
        "code_to_document",
        "existing_docs",
        "doc_templates"
      ],
      "systemPrompt": "You are writing documentation. Be clear and concise. Include examples."
    },
    "performance_optimization": {
      "description": "Optimizing code for performance",
      "model": "claude-3-opus",
      "temperature": 0.3,
      "maxContext": 12000,
      "includeFiles": [
        "performance_profile",
        "current_implementation",
        "benchmarks"
      ],
      "systemPrompt": "You are optimizing performance. Measure first. Keep code readable."
    }
  },
  "modelDefaults": {
    "claude-3-haiku": {
      "maxTokens": 4000,
      "stopSequences": [
        "</result>"
      ]
    },
    "claude-3-sonnet": {
      "maxTokens": 8000,
      "stopSequences": [
        "</result>"
      ]
    },
    "claude-3-opus": {
      "maxTokens": 16000,
      "stopSequences": [
        "</result>"
      ]
    }
  },
  "contextRules": {
    "alwaysInclude": [
      ".cursorrules",
      "tsconfig.json",
      "package.json"
    ],
    "neverInclude": [
      "node_modules/**",
      "dist/**",
      "coverage/**",
      "*.log"
    ],
    "maxFileSize": 50000,
    "preferredFileOrder": [
      "interfaces",
      "types",
      "implementation",
      "tests",
      "documentation"
    ]
  }
}
