{
  "enabled": true,
  "strictMode": false,
  "excludePatterns": [
    "node_modules/**",
    ".git/**",
    "coverage/**",
    "dist/**",
    "build/**",
    "*.log",
    "*.tmp",
    "tests/test-fresh-install/**",
    "*.test.*",
    "*.spec.*"
  ],
  "rules": {
    "newFileRequiresDocumentation": {
      "enabled": true,
      "severity": "warning",
      "exceptions": [
        "test/**",
        "*.test.*",
        "*.spec.*",
        "tests/**"
      ]
    },
    "modifiedFileRequiresDocumentationUpdate": {
      "enabled": true,
      "severity": "warning",
      "threshold": "significant"
    },
    "publicApiRequiresDocumentation": {
      "enabled": true,
      "severity": "warning",
      "patterns": [
        "export function",
        "export class",
        "export interface",
        "export type"
      ]
    },
    "documentationQualityStandards": {
      "enabled": true,
      "severity": "warning",
      "minLength": 50,
      "requiresExamples": true,
      "requiresParameters": true
    },
    "followsTemplateStructure": {
      "enabled": true,
      "severity": "warning"
    },
    "minimumCoverageThreshold": {
      "enabled": true,
      "severity": "warning",
      "threshold": 50
    }
  },
  "enforcement": {
    "preCommit": true,
    "preMerge": true,
    "ci": true,
    "allowOverride": true,
    "overrideRequiresJustification": true
  },
  "output": {
    "format": "detailed",
    "showSuggestions": true,
    "showExamples": true
  }
}
